import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Section = styled.section`
  padding: 3.5rem 1rem 2.5rem 1rem;
  background: #f8f5f2;
`;
const TitleRow = styled.div`
  display: flex;
  align-items: baseline;
  justify-content: center;
  gap: 1.2rem;
  margin-bottom: 2.2rem;
`;
const TitleNew = styled.span`
  font-family: 'Playfair Display', serif;
  font-size: 2.1rem;
  color: #b08d57;
  font-weight: 600;
  letter-spacing: 0.01em;
`;
const TitleBest = styled.span`
  font-family: 'Playfair Display', serif;
  font-size: 2.1rem;
  color: #ece7df;
  font-weight: 600;
  letter-spacing: 0.01em;
`;
const Cards = styled.div`
  display: flex;
  justify-content: center;
  gap: 2rem;
  flex-wrap: wrap;
`;
const Card = styled(motion.div)`
  background: #fff;
  border-radius: 1.5rem;
  box-shadow: 0 4px 24px rgba(176,141,87,0.08);
  padding: 1.5rem 1.2rem 1.2rem 1.2rem;
  width: 210px;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;
const Img = styled.img`
  width: 110px;
  height: 110px;
  object-fit: cover;
  border-radius: 1rem;
  margin-bottom: 1rem;
`;
const Name = styled.h4`
  color: #7c5e3c;
  font-size: 1.08rem;
  margin-bottom: 0.5rem;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  text-align: center;
`;
const Price = styled.div`
  color: #b08d57;
  font-size: 1.05rem;
  font-weight: bold;
  font-family: 'Montserrat', sans-serif;
`;
const TryBadge = styled.div`
  position: absolute;
  top: 12px;
  right: 12px;
  background: #6b6e58;
  color: #fff;
  font-size: 0.85rem;
  font-family: 'Montserrat', sans-serif;
  padding: 0.3rem 0.9rem;
  border-radius: 1rem;
  font-weight: 600;
  box-shadow: 0 2px 8px rgba(107,110,88,0.08);
`;

const products = [
  {
    name: 'Chocolate Covered Cookie-wich',
    img: 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=300&q=80',
    price: '$6.99',
  },
  {
    name: 'Ice Cream Deluxe Cookie',
    img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=300&q=80',
    price: '$10.75',
  },
  {
    name: 'Ham Baguette Sandwich',
    img: 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=300&q=80',
    price: '$18.50',
    tryNow: true,
  },
  {
    name: 'Ham & Swiss Croissant',
    img: 'https://images.unsplash.com/photo-1464306076886-debca5e8a6b0?auto=format&fit=crop&w=300&q=80',
    price: '$8.99',
  },
];

export default function BestsellersSection() {
  return (
    <Section id="bestsellers">
      <TitleRow>
        <TitleNew>New</TitleNew>
        <TitleBest>Bestseller</TitleBest>
      </TitleRow>
      <Cards>
        {products.map((p, i) => (
          <Card
            key={p.name}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7, delay: i * 0.15 }}
            viewport={{ once: true }}
          >
            {p.tryNow && <TryBadge>TRY NOW</TryBadge>}
            <Img src={p.img} alt={p.name} />
            <Name>{p.name}</Name>
            <Price>{p.price}</Price>
          </Card>
        ))}
      </Cards>
    </Section>
  );
}
