import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Section = styled.section`
  position: relative;
  background: #fff7ed;
  padding: 4.5rem 1rem 2.5rem 1rem;
  margin-top: -60px;
  z-index: 1;
  @media (max-width: 700px) {
    padding: 2.5rem 0.5rem 1.5rem 0.5rem;
    margin-top: -30px;
  }
`;
const SvgCurve = styled.svg`
  position: absolute;
  top: -1px;
  left: 0;
  width: 100%;
  height: 60px;
  z-index: 2;
  @media (max-width: 700px) {
    height: 30px;
  }
`;
const FeaturesRow = styled(motion.div)`
  display: flex;
  justify-content: center;
  gap: 3.5rem;
  flex-wrap: wrap;
  max-width: 900px;
  margin: 0 auto;
  @media (max-width: 700px) {
    gap: 1.5rem;
  }
`;
const Feature = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 200px;
  background: rgba(255,255,255,0.7);
  border-radius: 1.2rem;
  padding: 1.2rem 1rem;
  box-shadow: 0 2px 12px rgba(176,141,87,0.04);
  @media (max-width: 700px) {
    max-width: 140px;
    padding: 0.7rem 0.5rem;
  }
`;
const Icon = styled.div`
  font-size: 2.2rem;
  margin-bottom: 1rem;
`;
const Title = styled.h3`
  font-size: 1.08rem;
  color: #6b6e58;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  margin-bottom: 0.4rem;
`;
const Desc = styled.p`
  font-size: 0.97rem;
  color: #7c5e3c;
  text-align: center;
  font-family: 'Montserrat', sans-serif;
`;

const features = [
  { icon: 'ðŸ¥—', title: 'Nutrition rich', desc: 'Nutrition rich delights, crafted to nourish your taste buds.' },
  { icon: 'ðŸ”¥', title: '100% baked', desc: 'Savouring 100% baked items from our oven to your plate.' },
  { icon: 'ðŸ“¦', title: 'Secure packing', desc: 'Enjoy peace of mind with our secure packing.' },
];

export default function FeaturesSection() {
  return (
    <Section>
      <SvgCurve viewBox="0 0 1440 60" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M0,60 Q720,0 1440,60 V0 H0 V60 Z" fill="#6b6e58" />
      </SvgCurve>
      <FeaturesRow
        initial={{ opacity: 0, y: 60 }}
        whileInView={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
      >
        {features.map((f, i) => (
          <Feature
            key={f.title}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7, delay: i * 0.2 }}
            viewport={{ once: true }}
          >
            <Icon>{f.icon}</Icon>
            <Title>{f.title}</Title>
            <Desc>{f.desc}</Desc>
          </Feature>
        ))}
      </FeaturesRow>
    </Section>
  );
}
