import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const FeaturesWrapper = styled.section`
  display: flex;
  justify-content: center;
  gap: 3rem;
  padding: 3rem 1rem 2rem 1rem;
  background: #fff;
  box-shadow: 0 2px 16px rgba(176,141,87,0.04);
`;
const Feature = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 180px;
`;
const Icon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 1rem;
`;
const Title = styled.h3`
  font-size: 1.1rem;
  color: #7c5e3c;
  margin-bottom: 0.5rem;
`;
const Desc = styled.p`
  font-size: 0.95rem;
  color: #b08d57;
  text-align: center;
`;

const features = [
  { icon: 'ðŸ¥—', title: 'Nutrition rich', desc: 'Wholesome ingredients for a healthy treat.' },
  { icon: 'ðŸ”¥', title: '100% baked', desc: 'Freshly baked every morning, always.' },
  { icon: 'ðŸ“¦', title: 'Secure packing', desc: 'Delivered safe and fresh to your door.' },
];

export default function FeaturesSection() {
  return (
    <FeaturesWrapper>
      {features.map((f, i) => (
        <Feature
          key={f.title}
          initial={{ opacity: 0, y: 40 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7, delay: i * 0.2 }}
          viewport={{ once: true }}
        >
          <Icon>{f.icon}</Icon>
          <Title>{f.title}</Title>
          <Desc>{f.desc}</Desc>
        </Feature>
      ))}
    </FeaturesWrapper>
  );
}
