import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Section = styled.section`
  padding: 3rem 1rem 2rem 1rem;
  background: #fff;
`;
const Title = styled.h2`
  font-family: 'Playfair Display', serif;
  font-size: 2rem;
  color: #b08d57;
  text-align: center;
  margin-bottom: 2rem;
`;
const Content = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  gap: 2.5rem;
`;
const Item = styled(motion.div)`
  max-width: 260px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const Img = styled.img`
  width: 140px;
  height: 140px;
  object-fit: cover;
  border-radius: 1.2rem;
  margin-bottom: 1rem;
`;
const Desc = styled.div`
  color: #7c5e3c;
  font-size: 1.05rem;
  text-align: center;
`;

const items = [
  {
    img: 'https://images.unsplash.com/photo-1502741338009-cac2772e18bc?auto=format&fit=crop&w=300&q=80',
    desc: 'Classic cookies, made with love and tradition.'
  },
  {
    img: 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=300&q=80',
    desc: 'Signature cookie-wich, a sweet delight.'
  },
  {
    img: 'https://images.unsplash.com/photo-1464306076886-debca5e8a6b0?auto=format&fit=crop&w=300&q=80',
    desc: 'Fresh bread rolls, soft and golden.'
  },
];

export default function SignatureSection() {
  return (
    <Section id="signature">
      <Title>Classic cookies . Cookie-wich . Fresh breadie rolls</Title>
      <Content>
        {items.map((item, i) => (
          <Item
            key={item.desc}
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7, delay: i * 0.2 }}
            viewport={{ once: true }}
          >
            <Img src={item.img} alt="Signature" />
            <Desc>{item.desc}</Desc>
          </Item>
        ))}
      </Content>
    </Section>
  );
}
