import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeroBg = styled.section`
  background: #6b6e58;
  min-height: 520px;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding-top: 2.5rem;
  padding-bottom: 0;
`;
const HeroTextWrap = styled.div`
  position: relative;
  z-index: 2;
  width: 100%;
  max-width: 900px;
  margin: 0 auto;
  text-align: center;
`;
const HeroTitle = styled(motion.h1)`
  font-family: 'Playfair Display', serif;
  font-size: 3.2rem;
  font-weight: 400;
  color: #fff;
  line-height: 1.05;
  letter-spacing: 0.01em;
  margin: 0;
  position: relative;
  span {
    display: block;
    font-size: 4.2rem;
    color: #fff;
    font-weight: 400;
    margin-top: 0.2em;
    margin-bottom: 0.2em;
  }
  .highlight {
    color: #f8f5f2;
    font-size: 4.2rem;
    font-weight: 500;
    letter-spacing: 0.01em;
    display: inline-block;
  }
`;
const HeroSub = styled(motion.p)`
  color: #f8f5f2;
  font-size: 1.1rem;
  font-family: 'Montserrat', sans-serif;
  margin: 1.5rem auto 0.5rem auto;
  max-width: 420px;
`;
const HeroBtn = styled(motion.button)`
  margin-top: 1.2rem;
  background: #fff;
  color: #6b6e58;
  border: none;
  border-radius: 2rem;
  padding: 0.7rem 2.2rem;
  font-size: 1.1rem;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 2px 8px rgba(0,0,0,0.07);
  transition: background 0.2s, color 0.2s;
  &:hover {
    background: #b08d57;
    color: #fff;
  }
`;
const HeroImgWrap = styled.div`
  position: absolute;
  left: 50%;
  top: 54%;
  transform: translate(-50%, -10%);
  z-index: 3;
  width: 340px;
  max-width: 90vw;
`;
const HeroImg = styled(motion.img)`
  width: 100%;
  border-radius: 1.5rem;
  box-shadow: 0 8px 32px rgba(124,94,60,0.18);
  background: #fff;
`;
const Badge = styled(motion.div)`
  position: absolute;
  left: 8%;
  top: 38%;
  background: transparent;
  border: 2px solid #f8f5f2;
  color: #f8f5f2;
  border-radius: 50%;
  width: 80px;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'Montserrat', sans-serif;
  font-size: 0.95rem;
  font-weight: 500;
  text-align: center;
  z-index: 4;
  pointer-events: none;
`;

export default function HeroSection() {
  return (
    <HeroBg>
      <HeroTextWrap>
        <HeroTitle
          initial={{ y: 60, opacity: 0 }}
          whileInView={{ y: 0, opacity: 1 }}
          transition={{ duration: 1 }}
          viewport={{ once: true }}
        >
          The sweetest<br />
          Haven <span>for</span>
          <span className="highlight">freshly baked</span>
        </HeroTitle>
        <HeroSub
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
          viewport={{ once: true }}
        >
          A delightful treat that combines the comforting charm of freshly baked cookies with the rich, luxurious and mouthwatering fillings.
        </HeroSub>
        <HeroBtn
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7, delay: 0.5 }}
          viewport={{ once: true }}
        >
          Get more details →
        </HeroBtn>
      </HeroTextWrap>
      <HeroImgWrap>
        <HeroImg
          src="https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=600&q=80"
          alt="Bakery Stack"
          initial={{ scale: 0.8, opacity: 0 }}
          whileInView={{ scale: 1, opacity: 1 }}
          transition={{ duration: 1, delay: 0.2 }}
          viewport={{ once: true }}
        />
      </HeroImgWrap>
      <Badge
        initial={{ opacity: 0, scale: 0.7 }}
        whileInView={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.7, delay: 0.7 }}
        viewport={{ once: true }}
      >
        BEST SELLER<br />
        SINCE 1884
      </Badge>
    </HeroBg>
  );
}
