import React, { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';

const HeroBg = styled.section`
  background: #6b6e58;
  min-height: 520px;
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding-top: 2.5rem;
  padding-bottom: 0;
  overflow: hidden;
`;
const HeroTextWrap = styled.div`
  position: absolute;
  top: 18%;
  left: 50%;
  transform: translateX(-50%);
  z-index: 3;
  width: 100vw;
  max-width: 900px;
  text-align: center;
  pointer-events: none;
`;
const HeroTitle = styled(motion.h1)`
  font-family: 'Playfair Display', serif;
  font-size: 3.2rem;
  font-weight: 400;
  color: #fff;
  line-height: 1.05;
  letter-spacing: 0.01em;
  margin: 0;
  position: relative;
  text-shadow: 0 4px 24px rgba(0,0,0,0.18);
  span {
    display: block;
    font-size: 4.2rem;
    color: #fff;
    font-weight: 400;
    margin-top: 0.2em;
    margin-bottom: 0.2em;
  }
  .highlight {
    color: #f8f5f2;
    font-size: 4.2rem;
    font-weight: 500;
    letter-spacing: 0.01em;
    display: inline-block;
  }
  @media (max-width: 700px) {
    font-size: 2.1rem;
    span, .highlight { font-size: 2.5rem; }
  }
`;
const HeroSub = styled(motion.p)`
  color: #f8f5f2;
  font-size: 1.1rem;
  font-family: 'Montserrat', sans-serif;
  margin: 1.5rem auto 0.5rem auto;
  max-width: 420px;
  text-shadow: 0 2px 8px rgba(0,0,0,0.12);
  @media (max-width: 700px) {
    font-size: 0.98rem;
  }
`;
const HeroBtn = styled(motion.button)`
  margin-top: 1.2rem;
  background: #fff;
  color: #6b6e58;
  border: none;
  border-radius: 2rem;
  padding: 0.7rem 2.2rem;
  font-size: 1.1rem;
  font-family: 'Montserrat', sans-serif;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 2px 8px rgba(0,0,0,0.07);
  transition: background 0.2s, color 0.2s;
  &:hover {
    background: #b08d57;
    color: #fff;
  }
`;
const HeroImgWrap = styled.div`
  position: relative;
  z-index: 2;
  width: 340px;
  max-width: 90vw;
  margin-top: 80px;
  @media (max-width: 700px) {
    width: 220px;
    margin-top: 60px;
  }
`;
const HeroImg = styled(motion.img)`
  width: 100%;
  border-radius: 1.5rem;
  box-shadow: 0 8px 32px rgba(124,94,60,0.18);
  background: #fff;
`;
const Badge = styled(motion.div)`
  position: absolute;
  left: 8%;
  top: 38%;
  background: transparent;
  border: 2px solid #f8f5f2;
  color: #f8f5f2;
  border-radius: 50%;
  width: 80px;
  height: 80px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'Montserrat', sans-serif;
  font-size: 0.95rem;
  font-weight: 500;
  text-align: center;
  z-index: 4;
  pointer-events: none;
  @media (max-width: 700px) {
    width: 54px;
    height: 54px;
    font-size: 0.7rem;
    left: 2%;
    top: 30%;
  }
`;

// Falling cookies animation
const fall = keyframes`
  0% { transform: translateY(-120px) rotate(0deg) scale(0.7); opacity: 0; }
  10% { opacity: 1; }
  100% { transform: translateY(110vh) rotate(360deg) scale(1); opacity: 0.7; }
`;
const FallingCookie = styled.img`
  position: absolute;
  top: -80px;
  left: ${({ x }) => x}%;
  width: ${({ size }) => size}px;
  height: ${({ size }) => size}px;
  pointer-events: none;
  z-index: 10;
  animation: ${fall} ${({ duration }) => duration}s linear;
  animation-delay: ${({ delay }) => delay}s;
  opacity: 0.8;
`;

function getRandom(min, max) {
  return Math.random() * (max - min) + min;
}

export default function HeroSection() {
  // Generate falling cookies on mount
  const [cookies, setCookies] = useState([]);
  useEffect(() => {
    const arr = [];
    for (let i = 0; i < 12; i++) {
      arr.push({
        id: i,
        x: getRandom(5, 95),
        size: getRandom(32, 60),
        duration: getRandom(2.5, 4.5),
        delay: getRandom(0, 1.5),
      });
    }
    setCookies(arr);
  }, []);

  return (
    <HeroBg>
      {/* Falling cookies */}
      <AnimatePresence>
        {cookies.map(c => (
          <FallingCookie
            key={c.id}
            src="/cookie.png"
            x={c.x}
            size={c.size}
            duration={c.duration}
            delay={c.delay}
            style={{ animationFillMode: 'forwards' }}
          />
        ))}
      </AnimatePresence>
      <HeroImgWrap>
        <HeroImg
          src="/cookie.png"
          alt="Bakery Cookie"
          initial={{ scale: 0.8, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          transition={{ duration: 1, delay: 0.2 }}
        />
      </HeroImgWrap>
      <HeroTextWrap>
        <HeroTitle
          initial={{ y: 60, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1 }}
        >
          The sweetest<br />
          Haven <span>for</span>
          <span className="highlight">freshly baked</span>
        </HeroTitle>
        <HeroSub
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 0.3 }}
        >
          A delightful treat that combines the comforting charm of freshly baked cookies with the rich, luxurious and mouthwatering fillings.
        </HeroSub>
        <HeroBtn
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.7, delay: 0.5 }}
        >
          Get more details â†’
        </HeroBtn>
      </HeroTextWrap>
      <Badge
        initial={{ opacity: 0, scale: 0.7 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.7, delay: 0.7 }}
      >
        BEST SELLER<br />
        SINCE 1884
      </Badge>
    </HeroBg>
  );
}
