import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const Section = styled.section`
  padding: 3rem 1rem 2rem 1rem;
  background: #f8f5f2;
`;
const Title = styled.h2`
  font-family: 'Playfair Display', serif;
  font-size: 2.2rem;
  color: #b08d57;
  text-align: center;
  margin-bottom: 2rem;
`;
const Cards = styled.div`
  display: flex;
  justify-content: center;
  gap: 2rem;
  flex-wrap: wrap;
`;
const Card = styled(motion.div)`
  background: #fff;
  border-radius: 1.5rem;
  box-shadow: 0 4px 24px rgba(176,141,87,0.08);
  padding: 1.5rem;
  width: 220px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;
const Img = styled.img`
  width: 120px;
  height: 120px;
  object-fit: cover;
  border-radius: 1rem;
  margin-bottom: 1rem;
`;
const Name = styled.h4`
  color: #7c5e3c;
  font-size: 1.1rem;
  margin-bottom: 0.5rem;
`;
const Price = styled.div`
  color: #b08d57;
  font-size: 1rem;
  font-weight: bold;
`;

const products = [
  {
    name: 'Chocolate Cookie Sandwich',
    img: 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=300&q=80',
    price: '$6.99',
  },
  {
    name: 'Mint Chip Stack',
    img: 'https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=300&q=80',
    price: '$10.75',
  },
  {
    name: 'Classic Croissant',
    img: 'https://images.unsplash.com/photo-1519864600265-abb23847ef2c?auto=format&fit=crop&w=300&q=80',
    price: '$8.99',
  },
];

export default function BestsellersSection() {
  return (
    <Section id="bestsellers">
      <Title>New . Bestseller</Title>
      <Cards>
        {products.map((p, i) => (
          <Card
            key={p.name}
            initial={{ opacity: 0, x: 60 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.7, delay: i * 0.2 }}
            viewport={{ once: true }}
          >
            <Img src={p.img} alt={p.name} />
            <Name>{p.name}</Name>
            <Price>{p.price}</Price>
          </Card>
        ))}
      </Cards>
    </Section>
  );
}
