import React from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeroWrapper = styled.section`
  min-height: 80vh;
  background: linear-gradient(120deg, #f8f5f2 60%, #b08d57 100%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
`;
const HeroText = styled(motion.h1)`
  font-family: 'Playfair Display', serif;
  font-size: 3rem;
  color: #7c5e3c;
  text-align: center;
  margin-bottom: 2rem;
  line-height: 1.1;
  max-width: 700px;
`;
const HeroImg = styled(motion.img)`
  width: 320px;
  max-width: 90vw;
  border-radius: 2rem;
  box-shadow: 0 8px 32px rgba(124,94,60,0.15);
  background: #fff;
`;

export default function HeroSection() {
  return (
    <HeroWrapper>
      <HeroText
        initial={{ y: 80, opacity: 0 }}
        whileInView={{ y: 0, opacity: 1 }}
        transition={{ duration: 1 }}
        viewport={{ once: true }}
      >
        The sweetest Haven for <br />
        <span style={{ color: '#b08d57' }}>freshly baked</span>
      </HeroText>
      <HeroImg
        src="https://images.unsplash.com/photo-1504674900247-0877df9cc836?auto=format&fit=crop&w=600&q=80"
        alt="Bakery Stack"
        initial={{ scale: 0.8, opacity: 0 }}
        whileInView={{ scale: 1, opacity: 1 }}
        transition={{ duration: 1, delay: 0.3 }}
        viewport={{ once: true }}
      />
    </HeroWrapper>
  );
}
