import React from 'react';
import styled from 'styled-components';

const HeaderBar = styled.header`
  width: 100%;
  padding: 1.2rem 2.5rem;
  background: #6b6e58;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: sticky;
  top: 0;
  z-index: 20;
`;
const Logo = styled.div`
  font-family: 'Playfair Display', serif;
  font-size: 2rem;
  font-weight: bold;
  color: #fff;
  letter-spacing: 0.02em;
`;
const Nav = styled.nav`
  display: flex;
  align-items: center;
  gap: 2.5rem;
`;
const NavLink = styled.a`
  color: #fff;
  font-size: 1.05rem;
  text-decoration: none;
  font-family: 'Montserrat', sans-serif;
  font-weight: 500;
  letter-spacing: 0.01em;
  transition: color 0.2s;
  &:hover {
    color: #b08d57;
  }
`;
const IconGroup = styled.div`
  display: flex;
  align-items: center;
  gap: 1.2rem;
  margin-left: 2rem;
`;

export default function Header() {
  return (
    <HeaderBar>
      <Logo>Bakerville</Logo>
      <Nav>
        <NavLink href="#">Our Story</NavLink>
        <NavLink href="#">Baker catalog</NavLink>
        <NavLink href="#">Shop now</NavLink>
        <NavLink href="#">Bulk orders</NavLink>
        <NavLink href="#">#bakerville</NavLink>
        <IconGroup>
          <span role="img" aria-label="cart" style={{fontSize:'1.3rem'}}>ðŸ›’</span>
          <span role="img" aria-label="user" style={{fontSize:'1.3rem'}}>ðŸ‘¤</span>
        </IconGroup>
      </Nav>
    </HeaderBar>
  );
}
